import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import RecipeCard from '../Components/RecipeCard';
import { AuthContext } from '../utils/Auth';
import RecipeDetailsModal from '../Components/RecipeDetailsModal';

const HomePage = ({ searchTerm, cuisineFilter, difficultyFilter, sortOption }) => {
  const navigate = useNavigate();
  const { user } = React.useContext(AuthContext);
  const [recipes, setRecipes] = useState([]);
  const [filteredRecipes, setFilteredRecipes] = useState([]);
  const [showOnlyMyRecipe, setShowOnlyMyRecipe] = useState(false);
  const [selectedRecipe, setSelectedRecipe] = useState(null);

  useEffect(() => {
    const savedRecipes = JSON.parse(localStorage.getItem('recipes')) || [];
    setRecipes(savedRecipes);
  }, []);

  useEffect(() => {
    let visibleRecipes = recipes;

    // Filter based on the "Show My Recipes" toggle
    if (showOnlyMyRecipe && user) {
      visibleRecipes = visibleRecipes.filter((recipe) => recipe.userEmail === user.email);
    }

    // Filter by search term
    if (searchTerm) {
      visibleRecipes = visibleRecipes.filter((recipe) =>
        recipe.name.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }

    // Filter by cuisine
    if (cuisineFilter) {
      visibleRecipes = visibleRecipes.filter((recipe) => recipe.cuisine === cuisineFilter);
    }

    // Filter by difficulty
    if (difficultyFilter) {
      visibleRecipes = visibleRecipes.filter((recipe) => recipe.difficulty === difficultyFilter);
    }

    // Sorting logic for time
    if (sortOption === 'time') {
      visibleRecipes = visibleRecipes.sort((a, b) => {
        const timeToMinutes = (time) => {
          const [hours, minutes] = time.split(':').map(Number);
          return hours * 60 + minutes;
        };

        const timeA = timeToMinutes(a.time);
        const timeB = timeToMinutes(b.time);
        return timeA - timeB; // Sort in ascending order
      });
    } 
    // Sorting logic for alphabetical order
    else if (sortOption === 'alpha') {
      visibleRecipes = visibleRecipes.sort((a, b) => {
        const nameA = String(a.name).toLowerCase();
        const nameB = String(b.name).toLowerCase();
        return nameA.localeCompare(nameB);
      });
    }

    // Log visible recipes for debugging
    console.log("Filtered Recipes:", visibleRecipes);

    setFilteredRecipes(visibleRecipes); // Update the filteredRecipes state
  }, [recipes, showOnlyMyRecipe, searchTerm, cuisineFilter, difficultyFilter, sortOption]); // Dependencies include sortOption and other filters

  const handleDelete = (recipeId) => {
    const updatedRecipes = recipes.filter((recipe) => recipe.id !== recipeId);
    setRecipes(updatedRecipes); // Update the recipes state
    setFilteredRecipes(updatedRecipes); // Update the filteredRecipes state with the new list
    localStorage.setItem('recipes', JSON.stringify(updatedRecipes)); // Store the updated list in localStorage
  };

  return (
    <div>
      {/* User logged in, show the buttons for adding and filtering recipes */}
      {user && (
        <div style={{ display: 'flex', gap: '10px', marginBottom: '10px' }}>
          <button onClick={() => setShowOnlyMyRecipe((prev) => !prev)}>
            {showOnlyMyRecipe ? 'Show All Recipes' : 'Show My Recipes'}
          </button>
          <button onClick={() => navigate('/addRecipe')}>Add Recipe</button>
        </div>
      )}

      {/* No recipes found message */}
      {filteredRecipes.length === 0 ? (
        <p>No recipes found. Try adjusting your filters or add one!</p>
      ) : (
        <div style={gridStyle}>
          {/* Render recipe cards */}
          {filteredRecipes.map((r, index) => (
            <RecipeCard
              key={r.id || index} // Use `r.id` if available, otherwise fallback to `index`
              recipe={r}
              onDelete={handleDelete} // Pass handleDelete to RecipeCard
            />
          ))}
        </div>
      )}

      {/* Show Recipe Details Modal */}
      {selectedRecipe && (
        <RecipeDetailsModal
          recipe={selectedRecipe}
          onClose={() => setSelectedRecipe(null)} // Close the modal
        />
      )}
    </div>
  );
};

const gridStyle = {
  display: 'grid',
  gridTemplateColumns: 'repeat(auto-fill, minmax(250px, 1fr))',
  gap: '20px',
};

export default HomePage;
