import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import RecipeCard from '../Components/RecipeCard'; 
import { AuthContext } from '../utils/Auth'; 
import RecipeDetailsModal from '../Components/RecipeDetailsModal';

const HomePage = () => {
  const navigate = useNavigate();
  const { user } = React.useContext(AuthContext); 
  const [recipes, setRecipes] = useState([]);
//   const [filteredRecipes, setFilteredRecipes] = useState([]);
  const [showOnlyMyRecipe, setShowOnlyMyRecipe] = useState(false);
  const [selectedRecipe, setSelectedRecipe] = useState(null);
  const [allRecipe,setAllRecipe] = useState([]);

  const visibleRecipe = allRecipe.filter((r) => {
    if (!user) return true; // Show all recipes if not logged in
    if (showOnlyMyRecipe) return r.userId === user.email; // Only show user's recipes if 'Show My Recipes' is selected
    return true;
  })

  const filteredRecipes = visibleRecipe
    .filter((recipe) =>
      recipe.name.toLowerCase().includes(props?.searchTerm.toLowerCase())
    )
    .filter((r) => (props?.cuisineFilter ? r.cuisine === props?.cuisineFilter : true))
    .filter((r) => (props?.difficultyFilter ? r.difficulty === props?.difficultyFilter : true))
    .sort((a, b) => {
      if (props?.sortOption === "time") {
        return Number(a.time || 0) - Number(b.time || 0);
      }
      if (props?.sortOption === "alpha") {
        return a.name.localeCompare(b.name);
      }
      return 0;
    });

  useEffect(() => {
    // Load recipes from localStorage or API
    const savedRecipes = JSON.parse(localStorage.getItem('recipes')) || [];
    setRecipes(savedRecipes);
    filteredRecipes(); 
  }, []);

  useEffect(() => {
    // Filter recipes based on the "Show My Recipes" toggle
    if (showOnlyMyRecipe && user) {
      const myRecipes = recipes.filter((recipe) => recipe.userEmail === user.email);
      setFilteredRecipes(myRecipes);
    } else {
      setFilteredRecipes(recipes);
    }
  }, [showOnlyMyRecipe, recipes, user]);

  const handleDelete = (recipeId) => {
    // Filter out the deleted recipe by ID
    const updatedRecipes = recipes.filter((recipe) => recipe.id !== recipeId);
    
    // Update state with the new list
    setRecipes(updatedRecipes);
    filteredRecipes(updatedRecipes);

    // Update localStorage with the new list
    localStorage.setItem('recipes', JSON.stringify(updatedRecipes));
  };
  


  return (
    <div>
      {/* User logged in, show the buttons for adding and filtering recipes */}
      {user && (
        <div style={{ display: 'flex', gap: '10px', marginBottom: '10px' }}>
          <button onClick={() => setShowOnlyMyRecipe((prev) => !prev)}>
            {showOnlyMyRecipe ? 'Show All Recipes' : 'Show My Recipes'}
          </button>
          <button
            onClick={() => navigate('/addRecipe')} // Navigate to the form to add a new recipe
          >
            Add Recipe
          </button>
        </div>
      )}

      {/* No recipes found message */}
      {filteredRecipes.length === 0 ? (
        <p>No recipes found. Try adjusting your filters or add one!</p>
      ) : (
        <div style={gridStyle}>
          {/* Render recipe cards */}
          {filteredRecipes.map((r) => (
            <RecipeCard
              key={r.id}
              recipe={r}
              onDelete={handleDelete} // Pass handleDelete to RecipeCard
            />
          ))}
        </div>
      )}

      {/* Show Recipe Details Modal */}
      {selectedRecipe && (
        <RecipeDetailsModal
          recipe={selectedRecipe}
          onClose={() => setSelectedRecipe(null)} // Close the modal
        />
      )}
    </div>
  );
};

const gridStyle = {
  display: 'grid',
  gridTemplateColumns: 'repeat(auto-fill, minmax(250px, 1fr))',
  gap: '20px',
};

export default HomePage;

//edit recipe flow setup
//some minnor issue fixed.
