import React, { useState } from 'react';

const RecipeCard = () => {
    const [recipe, setRecipe] = useState({});

    // Ensure that recipe.time is defined before splitting
    const formattedTime = recipe.time ? recipe.time.split(":") : ["0", "00"];
    const [hours, minutes] = formattedTime;
    const formattedTimeStr = `${parseInt(hours, 10)}:${minutes.padStart(2, "0")}`;

    const onEdit = () => {
        // Handle edit logic here
    }

    const onDelete = () => {
        // Handle delete logic here
    }

    return (
        <div
            //   onClick={props?.onClick}
            style={{
                border: '1px solid #ddd',
                padding: '16px',
                borderRadius: '8px',
                backgroundColor: '#fff',
                display: 'flex',
                flexDirection: 'column',
                justifyContent: 'space-between',
                cursor: 'pointer',
                transition: 'box-shadow 0.3s',
            }}
        >
            <div>
                <h3>{recipe.name}</h3>
                <p><b>Cuisine:</b> {recipe.cuisine}</p>
                <p><b>Cooking Time:</b> {formattedTimeStr}</p> {/* Show the formatted time */}
            </div>

            {/* Show buttons if the user owns the recipe */}
            <div
                onClick={(e) => e.stopPropagation()}
                style={{
                    marginTop: '10px',
                    display: 'flex',
                    justifyContent: 'space-between'
                }}
            >
                <button onClick={onEdit} style={{ backgroundColor: '#f0ad4e' }}>Edit</button>
                <button onClick={onDelete} style={{ backgroundColor: '#d9534f' }}>Delete</button>
            </div>

        </div>
    );
};

export default RecipeCard;
