import React, { useEffect, useState } from 'react';
import { Rating } from 'react-simple-star-rating';

const RecipeDetailsModal = (props) => {
  const [showRating,setShowRating] = useState(false);//star batave
  // console.log("setShowRating",showRating);
  
  const [tempRating, setTempRating] = useState(0);
  // console.log("setTempRating",tempRating);
  
  // const [finalRating, setFinalRating] = useState(0);
  // console.log("setFinalRating",finalRating);
  

  useEffect(() => {
    // const initialRating = props?.recipe?.rating || 0;
    // setFinalRating(props?.recipe?.rating || 0);
    setTempRating(props?.recipe?.rating || 0);
    setShowRating(false);//star mode close
  }, [props?.recipe]);//recipe change thay tyare chale

  if (!props?.recipe) return null;

  const rateButtonLabel = tempRating > 0 ? "Edit recipe Rate" : "Add recipe Rate";
  //rating hoi to label edit show thase baki add show

  const modalOverlayStyle = {
    position: "fixed",
    top: 0,
    left: 0,
    width: "100vw",
    height: "100vh",
    backgroundColor: "rgba(0, 0, 0, 0.6)",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    zIndex: 1000,
  };

  const modalContentStyle = {
    backgroundColor: "#fff",
    padding: "20px",
    borderRadius: "8px",
    width: "90%",
    maxWidth: "500px",
    maxHeight: "80vh",
    overflowY: "auto",
    position: "relative",
  };

  const closeButtonStyle = {
    position: "absolute",
    top: "10px",
    right: "10px",
    background: "red",
    color: "white",
    border: "none",
    borderRadius: "4px",
    padding: "5px 10px",
    cursor: "pointer",
  };

  // const handleClose = () => {
  //   setIsOpen(false);
  // };

  const handleRateButtonClick = () => {
      setShowRating(true);
      //user add/edit btn click kare tyare rating edit ui mate true
  }

  const handleRatingChange = (rate) => {
    setTempRating(rate);
    //user rating star click tyare te rating tempRating ma set
  };

  const handleSave = () => {
    const recipes = JSON.parse(localStorage.getItem('recipes')) || [];//recipe get [{}]
    const updatedRecipes = recipes.map((r) =>
      r.id === props?.recipe.id ? { ...r, rating: tempRating } : r
    );//
    localStorage.setItem('recipes', JSON.stringify(updatedRecipes));//store recipe
    console.log("updatedRecipes",localStorage.setItem('recipes', JSON.stringify(updatedRecipes)));
    
    setTempRating(tempRating);
    setShowRating(false);
    if (props?.onRatingUpdate) props?.onRatingUpdate();
  };

  return (
    <div style={modalOverlayStyle}>
      <div style={modalContentStyle}>
        <button style={closeButtonStyle} onClick={props?.onClose}>X</button>
        <h2>{props.recipe.name}</h2>
        <div style={{ marginTop: '10px' }}>
          <button
            onClick={handleRateButtonClick}
              style={{padding: '8px 16px',backgroundColor: '#ed2929ff',color: '#fff',border: 'none',borderRadius: '4px',
              cursor: 'pointer',}}>
            {rateButtonLabel}
          </button>
        </div>
        {/* Show rating stars */}
        {/* editable rating stars and save btn */}
        {showRating ? (
  <div style={{ marginTop: '10px'}}>
    <Rating
      onClick={handleRatingChange}
      size={30}
      initialValue={tempRating}
      allowHover
      allowFraction={false}
      transition
      fillColor="#FFD700"
      emptyColor="#fff"
      SVGstyle={{ stroke: 'black', strokeWidth: 2, margin: 2 }}
    />

    <button
      onClick={handleSave}
      style={{
        marginTop: '10px',
        padding: '8px 16px',
        backgroundColor: '#28a745',
        color: '#fff',
        border: 'none',
        borderRadius: '4px',
        cursor: 'pointer',
      }}
      disabled={tempRating === 0}
    >
      Save
    </button>
  </div>
) : (
  // finalrating > 0 readOnly rating show
  tempRating > 0 && (
    <div style={{ marginTop: '10px' }}>
      <p><strong>Your Rating:</strong></p>
      <Rating
        readonly
        size={25}
        initialValue={tempRating}
        fillColor="#FFD700"
        emptyColor="#fff"
        SVGstyle={{ stroke: 'black', strokeWidth: 2, margin: 2 }}
      />
    </div>
  )
)}
        
        <p><strong>Cuisine:</strong> {props.recipe.cuisine}</p>
        <p><strong>Difficulty Level:</strong> {props.recipe.difficulty}</p>
        <p><strong>Time:</strong> {props.recipe.time} hour</p>
        <p><strong>Instructions: </strong>{props.recipe.instructions}</p>
        {/* Ingredients can be displayed here if needed */}
        <p>Ingredients:</p>
        <ul>
          {props?.recipe.ingredients.map((ingredient, index) => (
            <li key={index}>{ingredient}</li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default RecipeDetailsModal;
//save rating localstorage and recipeDetailsModal
