import React, { useEffect, useState, useContext } from 'react';
import { Rating } from 'react-simple-star-rating';
import { AuthContext } from '../utils/Auth';

const RecipeDetailsModal = ({ recipe, onClose, onRatingUpdate }) => {
  const { user } = useContext(AuthContext);
  const [showRating, setShowRating] = useState(false);
  const [tempRating, setTempRating] = useState(0);
  const [originalRating, setOriginalRating] = useState(0);
  const [saveMessage, setSaveMessage] = useState('');
  const [loading, setLoading] = useState(false);
  const [ratingLoaded, setRatingLoaded] = useState(false);

  useEffect(() => {
    const fetchUserRating = async () => {
      if (!recipe?._id || !user?.email) return;

      try {
        const res = await fetch(`http://localhost:5000/api/recipes/${recipe._id}`);
        const data = await res.json();
        const ratingObj = data.rating?.find(r => r.userEmail === user.email);
        const value = ratingObj ? ratingObj.rating : 0;

        setTempRating(value);
        setOriginalRating(value);
      } catch (err) {
        console.error('Error fetching user rating:', err);
        setTempRating(0);
        setOriginalRating(0);
      } finally {
        setRatingLoaded(true);
        setShowRating(false);
      }
    };

    fetchUserRating();
  }, [recipe, user?.email]);

  const handleSave = async () => {
    setLoading(true);
    try {
      const res = await fetch(`http://localhost:5000/api/recipes/${recipe._id}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ userEmail: user.email, rating: tempRating }),
      });
      if (!res.ok) throw new Error('Failed to save rating');
      setOriginalRating(tempRating);
      setSaveMessage('Rating Saved!');
      if (onRatingUpdate) onRatingUpdate();
    } catch (err) {
      console.error('Save rating error:', err);
      setSaveMessage('Save failed. Try again.');
    } finally {
      setLoading(false);
      setShowRating(false);
    }
  };

  const rateButtonLabel = originalRating > 0 ? 'Edit Rating' : 'Add Rating';

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <button className="close-btn" onClick={onClose}>X</button>
        <h2>{recipe.name}</h2>
        <button onClick={() => setShowRating(true)}>
          {rateButtonLabel}
        </button>

        {showRating ? (
          <div>
            <Rating
              onClick={setTempRating}
              initialValue={tempRating}
              size={30}
              allowFraction={false}
              transition
              fillColor="#FFD700"
              emptyColor="#ccc"
            />
            <button onClick={handleSave} disabled={loading}>
              {loading ? 'Saving...' : 'Save Rating'}
            </button>
            {saveMessage && <p style={{ color: saveMessage.includes('Saved') ? 'green' : 'red' }}>{saveMessage}</p>}
          </div>
        ) : (
          ratingLoaded && (
            <div>
              <p>Your Rating:</p>
              <Rating
                readonly
                initialValue={originalRating}
                size={25}
                fillColor="#FFD700"
                emptyColor="#ccc"
              />
            </div>
          )
        )}

        {/* Additional recipe details */}
        <p><strong>Cuisine:</strong> {recipe.cuisine}</p>
        <p><strong>Difficulty:</strong> {recipe.difficulty}</p>
        <p><strong>Time:</strong> {recipe.time} hour{recipe.time > 1 ? 's' : ''}</p>
        <p><strong>Instructions:</strong> {recipe.instructions}</p>
        <p><strong>Ingredients:</strong></p>
        <ul>{recipe.ingredients.map((i, idx) => <li key={idx}>{i}</li>)}</ul>
      </div>
    </div>
  );
};

export default RecipeDetailsModal;
