import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const RecipeCard = ({ recipe }) => {
  const [isEditing, setIsEditing] = useState(false); // State to track if the recipe is being edited
  const [updatedRecipe, setUpdatedRecipe] = useState(recipe); // The current recipe state for editing
  const navigate = useNavigate();

  // Split time into hours and minutes for easier editing
  const [hours, minutes] = updatedRecipe.time.split(":");
  const formattedTime = `${parseInt(hours, 10)}:${minutes.padStart(2, "0")}`;

  // Edit the recipe
  const handleEdit = () => {
    setIsEditing(true); // Enable editing
    navigate('/addRecipe', { state: { recipe: updatedRecipe } }); // Pass the recipe data for editing
  };

  // Save the changes to the recipe
  const handleSaveEdit = () => {
    const updatedRecipes = JSON.parse(localStorage.getItem('recipes')) || [];
    const index = updatedRecipes.findIndex(r => r.id === updatedRecipe.id);
    updatedRecipes[index] = updatedRecipe; // Replace the old recipe with the updated one
    localStorage.setItem('recipes', JSON.stringify(updatedRecipes));
    setIsEditing(false); // Exit editing mode
  };

  // Handle recipe delete
  const handleDelete = () => {
    const updatedRecipes = JSON.parse(localStorage.getItem('recipes')) || [];
    const filteredRecipes = updatedRecipes.filter(r => r.id !== updatedRecipe.id);
    localStorage.setItem('recipes', JSON.stringify(filteredRecipes));
  };

  // Handle input changes for the edit form
  const handleChange = (e) => {
    const { name, value } = e.target;
    setUpdatedRecipe((prevRecipe) => ({
      ...prevRecipe,
      [name]: value,
    }));
  };

  // Handle ingredient changes (edit, add, remove)
  const handleIngredientChange = (index, value) => {
    const updatedIngredients = [...updatedRecipe.ingredients];
    updatedIngredients[index] = value;
    setUpdatedRecipe({
      ...updatedRecipe,
      ingredients: updatedIngredients,
    });
  };

  const addIngredient = () => {
    setUpdatedRecipe({
      ...updatedRecipe,
      ingredients: [...updatedRecipe.ingredients, ''],
    });
  };

  const removeIngredient = (index) => {
    const updatedIngredients = updatedRecipe.ingredients.filter((_, i) => i !== index);
    setUpdatedRecipe({
      ...updatedRecipe,
      ingredients: updatedIngredients,
    });
  };

  return (
    <div
      style={{
        border: '1px solid #ddd',
        padding: '16px',
        borderRadius: '8px',
        backgroundColor: '#fff',
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'space-between',
        cursor: 'pointer',
        transition: 'box-shadow 0.3s',
      }}
    >
      <div>
        {isEditing ? (
          <>
            <input
              type="text"
              name="name"
              value={updatedRecipe.name}
              onChange={handleChange}
              placeholder="Recipe Name"
            />
            <input
              type="text"
              name="cuisine"
              value={updatedRecipe.cuisine}
              onChange={handleChange}
              placeholder="Cuisine"
            />
            <div style={{ display: 'flex', gap: '1rem', marginBottom: '10px' }}>
              <input
                type="number"
                name="hours"
                value={hours}
                onChange={(e) => {
                  const newTime = `${e.target.value}:${minutes}`;
                  setUpdatedRecipe({ ...updatedRecipe, time: newTime });
                }}
                placeholder="Hours"
                style={{ flex: 1 }}
              />
              <input
                type="number"
                name="minutes"
                value={minutes}
                onChange={(e) => {
                  const newTime = `${hours}:${e.target.value}`;
                  setUpdatedRecipe({ ...updatedRecipe, time: newTime });
                }}
                placeholder="Minutes"
                style={{ flex: 1 }}
              />
            </div>
            <textarea
              name="instructions"
              value={updatedRecipe.instructions}
              onChange={handleChange}
              placeholder="Instructions"
            />

            <div>
              <h4>Ingredients:</h4>
              {updatedRecipe.ingredients.map((ingredient, index) => (
                <div key={index} style={{ display: 'flex', gap: '10px', marginBottom: '5px' }}>
                  <input
                    type="text"
                    value={ingredient}
                    onChange={(e) => handleIngredientChange(index, e.target.value)}
                    placeholder={`Ingredient ${index + 1}`}
                    style={{ flex: 1 }}
                  />
                  <button
                    type="button"
                    onClick={() => removeIngredient(index)}
                    style={{ background: 'red', color: 'white', border: 'none', borderRadius: '4px' }}
                  >
                    X
                  </button>
                </div>
              ))}
              <button onClick={addIngredient}>Add Ingredient</button>
            </div>

            <div style={{ marginTop: '10px' }}>
              <h4>Difficulty Level:</h4>
              <select
                name="difficulty"
                value={updatedRecipe.difficulty}
                onChange={handleChange}
                style={{ width: '100%' }}
              >
                <option value="Easy">Easy</option>
                <option value="Medium">Medium</option>
                <option value="Hard">Hard</option>
              </select>
            </div>

            <button onClick={handleSaveEdit} style={{ backgroundColor: '#5bc0de', marginTop: '10px' }}>
              Save
            </button>
          </>
        ) : (
          <>
            <h3>{updatedRecipe.name}</h3>
            <p><b>Cuisine:</b> {updatedRecipe.cuisine}</p>
            <p><b>Cooking Time:</b> {formattedTime}</p>
            <p><b>Instructions:</b> {updatedRecipe.instructions}</p>
            <p><b>Ingredients:</b> {updatedRecipe.ingredients.join(", ")}</p>
            <p><b>Difficulty Level:</b> {updatedRecipe.difficulty}</p>
            <button onClick={handleEdit} style={{ backgroundColor: '#f0ad4e' }}>Edit</button>
            <button onClick={handleDelete} style={{ backgroundColor: '#d9534f' }}>Delete</button>
          </>
        )}
      </div>
    </div>
  );
};

export default RecipeCard;
