import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import RecipeCard from '../Components/RecipeCard'; // Assuming RecipeCard is another component
import { AuthContext } from '../utils/Auth'; // Assuming this is your auth context
import RecipeDetailsModal from '../Components/RecipeDetailsModal';

const HomePage = () => {
  const navigate = useNavigate();
  const { user } = React.useContext(AuthContext); // Access the user from the AuthContext
  const [recipes, setRecipes] = useState([]);
  const [filteredRecipes, setFilteredRecipes] = useState([]);
  const [showOnlyMyRecipe, setShowOnlyMyRecipe] = useState(false);
  const [selectedRecipe, setSelectedRecipe] = useState(null);
  const [editingRecipe, setEditingRecipe] = useState(null);

  useEffect(() => {
    // Load recipes from localStorage or API
    const savedRecipes = JSON.parse(localStorage.getItem('recipes')) || [];
    setRecipes(savedRecipes);
    setFilteredRecipes(savedRecipes); // Initially show all recipes
  }, []);

  useEffect(() => {
    // Filter recipes based on the "Show My Recipes" toggle
    if (showOnlyMyRecipe && user) {
      const myRecipes = recipes.filter((recipe) => recipe.userEmail === user.email);
      setFilteredRecipes(myRecipes);
    } else {
      setFilteredRecipes(recipes);
    }
  }, [showOnlyMyRecipe, recipes, user]);

  const handleDelete = (recipeToDelete) => {
    const updatedRecipes = recipes.filter((recipe) => recipe.id !== recipeToDelete.id);
    setRecipes(updatedRecipes);
    setFilteredRecipes(updatedRecipes);
    localStorage.setItem('recipes', JSON.stringify(updatedRecipes));
  };

  return (
    <div>
      {/* User logged in, show the buttons for adding and filtering recipes */}
      {user && (
        <div style={{ display: 'flex', gap: '10px', marginBottom: '10px' }}>
          <button onClick={() => setShowOnlyMyRecipe((prev) => !prev)}>
            {showOnlyMyRecipe ? 'Show All Recipes' : 'Show My Recipes'}
          </button>
          <button
            onClick={() => {
              setEditingRecipe(null);
              navigate('/addRecipe'); // Navigate to the form to add a new recipe
            }}
          >
            Add Recipe
          </button>
        </div>
      )}

      {/* No recipes found message */}
      {filteredRecipes.length === 0 ? (
        <p>No recipes found. Try adjusting your filters or add one!</p>
      ) : (
        <div style={gridStyle}>
          {/* Render recipe cards */}
          {filteredRecipes.map((r) => (
            <RecipeCard
              key={r.id}
              recipe={r}
              onEdit={() => {
                setEditingRecipe(r); // Set the recipe to be edited
                navigate('/addRecipe'); // Navigate to edit page
              }}
              onDelete={() => handleDelete(r)} // Delete the recipe
              onClick={() => setSelectedRecipe(r)} // Show the recipe modal
              currentUserEmail={user?.email} // Pass the logged-in user's email
            />
          ))}
        </div>
      )}

      {/* Show Recipe Details Modal */}
      {selectedRecipe && (
        <RecipeDetailsModal
          recipe={selectedRecipe}
          onClose={() => setSelectedRecipe(null)} // Close the modal
        />
      )}
    </div>
  );
};

const gridStyle = {
  display: 'grid',
  gridTemplateColumns: 'repeat(auto-fill, minmax(250px, 1fr))',
  gap: '20px',
};


export default HomePage;
