import React, { useState } from 'react';

const RecipeForm = ({ addRecipe }) => {
  const [name, setName] = useState('');
  const [cuisine, setCuisine] = useState('');
  const [ingredients, setIngredients] = useState(['']);
  const [instructions, setInstructions] = useState('');
  const [cookingTime, setCookingTime] = useState('');
  const [difficulty, setDifficulty] = useState('easy');

  const handleIngredientChange = (index, value) => {
    const updatedIngredients = [...ingredients];
    updatedIngredients[index] = value;
    setIngredients(updatedIngredients);
  };

  const addIngredient = () => {
    setIngredients([...ingredients, '']);
  };

  const removeIngredient = (index) => {
    const updatedIngredients = ingredients.filter((_, i) => i !== index);
    setIngredients(updatedIngredients);
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    const newRecipe = {
      name,
      cuisine,
      ingredients,
      instructions,
      cookingTime,
      difficulty,
    };

    addRecipe(newRecipe);
    setName('');
    setCuisine('');
    setIngredients(['']);
    setInstructions('');
    setCookingTime('');
    setDifficulty('easy');
  };

  return (
    <form onSubmit={handleSubmit}>
      <input type="text" value={name} onChange={(e) => setName(e.target.value)} placeholder="Recipe Name" required />
      <select value={cuisine} onChange={(e) => setCuisine(e.target.value)} required>
        <option value="">Select Cuisine</option>
        <option value="Italian">Italian</option>
        <option value="Mexican">Mexican</option>
        <option value="Asian">Asian</option>
        {/* Add more cuisines */}
      </select>

      {ingredients.map((ingredient, index) => (
        <div key={index}>
          <input
            type="text"
            value={ingredient}
            onChange={(e) => handleIngredientChange(index, e.target.value)}
            placeholder="Ingredient"
          />
          <button type="button" onClick={() => removeIngredient(index)}>Remove</button>
        </div>
      ))}
      <button type="button" onClick={addIngredient}>Add Ingredient</button>

      <textarea value={instructions} onChange={(e) => setInstructions(e.target.value)} placeholder="Instructions" required />
      <input
        type="number"
        value={cookingTime}
        onChange={(e) => setCookingTime(e.target.value)}
        placeholder="Cooking Time (in minutes)"
        required
      />
      
      <div>
        <label>
          <input type="radio" value="easy" checked={difficulty === 'easy'} onChange={() => setDifficulty('easy')} />
          Easy
        </label>
        <label>
          <input type="radio" value="medium" checked={difficulty === 'medium'} onChange={() => setDifficulty('medium')} />
          Medium
        </label>
        <label>
          <input type="radio" value="hard" checked={difficulty === 'hard'} onChange={() => setDifficulty('hard')} />
          Hard
        </label>
      </div>

      <button type="submit">Add Recipe</button>
    </form>
  );
};

export default RecipeForm;
