import React, { useContext, useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { AuthContext } from '../utils/Auth';

const RecipeCard = (props) => {
  const navigate = useNavigate();
  const { user } = useContext(AuthContext);
  const currentUserEmail = user?.email || '';
  const [avgRating, setAvgRating] = useState(0);
  const [tempRating, setTempRating] = useState(0);

  useEffect(() => {
    const storedRecipes = JSON.parse(localStorage.getItem('recipes')) || [];
    const recipe = storedRecipes.find(r => r.id === props.recipe.id);

    if (recipe && Array.isArray(recipe.ratings)) {
      const prevRatingObj = recipe.ratings.find(r => r.userEmail === currentUserEmail);
      const prevRating = prevRatingObj ? prevRatingObj.rating : 0;
      setTempRating(prevRating);

      const ratingsArr = recipe.ratings
        .map(r => r.rating)
        .filter(r => typeof r === 'number' && r > 0);

      if (ratingsArr.length > 0) {
        const sum = ratingsArr.reduce((acc, val) => acc + val, 0);
        const avg = sum / ratingsArr.length;
        setAvgRating(avg);
      } else {
        setAvgRating(0);
      }
    } else {
      setAvgRating(0);
      setTempRating(0);
    }
  }, [props.recipe, currentUserEmail]);

  const isUserOwner = user && user.email === props.recipe.userEmail;

  const onEdit = () => {
    navigate(`/editRecipe/${props.recipe.id}`);
  };

  const onDelete = () => {
    const storedRecipes = JSON.parse(localStorage.getItem('recipes')) || [];
    const updatedRecipes = storedRecipes.filter(recipe => recipe.id !== props.recipe.id);
    localStorage.setItem('recipes', JSON.stringify(updatedRecipes));
    if (props.onDelete) {
      props.onDelete(props.recipe.id);
    }
  };

  return (
    <div
      onClick={props.onClick}
      style={{
        border: '1px solid #ddd',
        padding: 16,
        borderRadius: 8,
        backgroundColor: '#fff',
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'space-between',
        cursor: 'pointer',
        transition: 'box-shadow 0.3s',
      }}
    >
      <div>
        <h3>{props.recipe.name}</h3>
        <p><b>Cuisine:</b> {props.recipe.cuisine}</p>
        <p><b>Cooking Time:</b> {props.recipe.time}</p>
        <p><b>Rating:</b> {avgRating.toFixed(1)} / 5</p>
        <p><b>Your Rating:</b> {tempRating > 0 ? tempRating : "Not rated yet"}</p>
      </div>

      {isUserOwner && (
        <div
          onClick={e => e.stopPropagation()}
          style={{ marginTop: 10, display: 'flex', justifyContent: 'space-between' }}
        >
          <button onClick={onEdit} style={{ backgroundColor: '#f0ad4e' }}>Edit</button>
          <button onClick={onDelete} style={{ backgroundColor: '#d9534f' }}>Delete</button>
        </div>
      )}
    </div>
  );
};

export default RecipeCard;


