import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const RecipeCard = ({ recipe }) => {
  const [isEditing, setIsEditing] = useState(false); // State to track if the recipe is being edited
  const [updatedRecipe, setUpdatedRecipe] = useState(recipe); // The current recipe state for editing
  const navigate = useNavigate();

  const [hours, minutes] = updatedRecipe.time.split(":");
  const formattedTime = `${parseInt(hours, 10)}:${minutes.padStart(2, "0")}`;

  // Edit the recipe
  const handleEdit = () => {
    setIsEditing(true); // Enable editing
    navigate('/addRecipe');
  };

  // Save the changes to the recipe
  const handleSaveEdit = () => {
    const updatedRecipes = JSON.parse(localStorage.getItem('recipes')) || [];
    const index = updatedRecipes.findIndex(r => r.id === updatedRecipe.id);
    updatedRecipes[index] = updatedRecipe; // Replace the old recipe with the updated one
    localStorage.setItem('recipes', JSON.stringify(updatedRecipes));
    setIsEditing(false); // Exit editing mode
  };

  // Handle recipe delete
  const handleDelete = () => {
    const updatedRecipes = JSON.parse(localStorage.getItem('recipes')) || [];
    const filteredRecipes = updatedRecipes.filter(r => r.id !== updatedRecipe.id);
    localStorage.setItem('recipes', JSON.stringify(filteredRecipes));
  };

  // Handle input changes for the edit form
  const handleChange = (e) => {
    const { name, value } = e.target;
    setUpdatedRecipe((prevRecipe) => ({
      ...prevRecipe,
      [name]: value,
    }));
  };

  return (
    <div
      style={{
        border: '1px solid #ddd',
        padding: '16px',
        borderRadius: '8px',
        backgroundColor: '#fff',
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'space-between',
        cursor: 'pointer',
        transition: 'box-shadow 0.3s',
      }}
    >
      <div>
        {isEditing ? (
          <>
            <input
              type="text"
              name="name"
              value={updatedRecipe.name}
              onChange={handleChange}
              placeholder="Recipe Name"
            />
            <input
              type="text"
              name="cuisine"
              value={updatedRecipe.cuisine}
              onChange={handleChange}
              placeholder="Cuisine"
            />
            <input
              type="text"
              name="time"
              value={updatedRecipe.time}
              onChange={handleChange}
              placeholder="Cooking Time"
            />
            <textarea
              name="instructions"
              value={updatedRecipe.instructions}
              onChange={handleChange}
              placeholder="Instructions"
            />
            <button onClick={handleSaveEdit}>Save</button>
          </>
        ) : (
          <>
            <h3>{updatedRecipe.name}</h3>
            <p><b>Cuisine:</b> {updatedRecipe.cuisine}</p>
            <p><b>Cooking Time:</b> {formattedTime}</p>
            <p><b>Instructions:</b> {updatedRecipe.instructions}</p>
            <button onClick={handleEdit} style={{ backgroundColor: '#f0ad4e' }}>Edit</button>
            <button onClick={handleDelete} style={{ backgroundColor: '#d9534f' }}>Delete</button>
          </>
        )}
      </div>
    </div>
  );
};

export default RecipeCard;
