import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const RecipeCard = (props) => {
    // const [recipe, setRecipe] = useState({});
    const navigate = useNavigate();

    const onEdit = () => {
        // Handle edit logic here
        navigate('/addRecipe');
    }

    const onDelete = () => {
        // Handle delete logic here
        onDelete(props?.recipe.id);
    }

    return (
        <div
            //   onClick={props?.onClick}
            style={{
                border: '1px solid #ddd',
                padding: '16px',
                borderRadius: '8px',
                backgroundColor: '#fff',
                display: 'flex',
                flexDirection: 'column',
                justifyContent: 'space-between',
                cursor: 'pointer',
                transition: 'box-shadow 0.3s',
            }}
        >
            <div>
                <h3>{props?.recipe.name}</h3>
                <p><b>Cuisine:</b> {props?.recipe.cuisine}</p>
                <p><b>Cooking Time:</b> {props?.recipe.time}</p> {/* Show the formatted time */}
            </div>

            {/* Show buttons if the user owns the recipe */}
            <div
                onClick={(e) => e.stopPropagation()}
                style={{
                    marginTop: '10px',
                    display: 'flex',
                    justifyContent: 'space-between'
                }}
            >
                <button onClick={onEdit} style={{ backgroundColor: '#f0ad4e' }}>Edit</button>
                <button onClick={onDelete} style={{ backgroundColor: '#d9534f' }}>Delete</button>
            </div>

        </div>
    );
};

export default RecipeCard;
