import React, { useContext, useEffect, useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import './App.css';
import Header from './Components/Header';
import { AuthProvider, AuthContext } from './utils/Auth';
import HomePage from './Pages/HomePage';
import LoginPage from './Pages/LoginPage';
import SignupPage from './Pages/SignupPage';
import RecipeForm from './Components/RecipeForm';

function AppWrapper() {
  return (
    <AuthProvider>
      <App />
    </AuthProvider>
  );
}
function App() {
  const { user } = useContext(AuthContext); // Get the user state from AuthContext

  // State variables for search and filters
  const [searchTerm, setSearchTerm] = useState('');
  const [cuisineFilter, setCuisineFilter] = useState('');
  const [difficultyFilter, setDifficultyFilter] = useState('');
  const [sortOption, setSortOption] = useState('');

  // State to hold the fetched recipe data
  const [allRecipes, setAllRecipes] = useState([]);

  // Fetch recipe data on mount
  useEffect(() => {
    const fetchRecipes = async () => {
      try {
        const response = await fetch('/api/recipes'); // Replace with your actual API endpoint
        const data = await response.json();
        setAllRecipes(data);
      } catch (error) {
        console.error('Error fetching recipes:', error);
      }
    };

    fetchRecipes();
  }, []); // Empty array to run only on mount

  // Deriving unique cuisines and difficulties from dynamic recipe data
  const uniqueCuisines = [...new Set(allRecipes.map((r) => r.cuisine))];
  const uniqueDifficulties = [...new Set(allRecipes.map((r) => r.difficulty))];

  return (
    <Router>
      <Header 
        searchTerm={searchTerm}
        setSearchTerm={setSearchTerm}
        cuisineFilter={cuisineFilter}
        setCuisineFilter={setCuisineFilter}
        difficultyFilter={difficultyFilter}
        setDifficultyFilter={setDifficultyFilter}
        sortOption={sortOption}
        setSortOption={setSortOption}
        uniqueCuisines={uniqueCuisines}
        uniqueDifficulties={uniqueDifficulties}
      />
      <main style={{ minHeight: '80vh', padding: '20px' }}>
        <Routes>
          {/* Home Page: Accessible by everyone */}
          <Route path="/" element={<HomePage />} />

          {/* Login & Signup routes only if the user is not logged in */}
          {!user && (
            <>
              <Route path="/login" element={<LoginPage />} />
              <Route path="/signup" element={<SignupPage />} />
            </>
          )}

          {/* If user is logged in, redirect them to home (prevent accessing login/signup) */}
          {user && <Route path="/login" element={<Navigate to="/" />} />}
          {user && <Route path="/signup" element={<Navigate to="/" />} />}

          {/* Protected Add Recipe Page */}
          <Route path="/addRecipe" element={user ? <RecipeForm /> : <Navigate to="/login" />} />
        </Routes>
      </main>
    </Router>
  );
}

export default AppWrapper;
