import React, { useEffect, useState } from 'react';
import { Rating } from 'react-simple-star-rating';

const RecipeDetailsModal = (props) => {
  const [showRating, setShowRating] = useState(false);
  const [tempRating, setTempRating] = useState(0); // temp value before saving
  const [finalRating, setFinalRating] = useState(0); // saved rating

  useEffect(() => {
    const stored = JSON.parse(localStorage.getItem('recipeRatings') || '{}');
    if (stored[props.recipe.id]) {
      setFinalRating(stored[props.recipe.id]);
    }
  }, [props.recipe.id]);

  if (!props?.recipe) return null;

  const handleRateClick = () => {
    setShowRating(true);
  };

  const handleRating = (rate) => {
    setTempRating(rate);
  };

  const handleSave = () => {
    setFinalRating(tempRating);
    setShowRating(false);

    // Save logic: e.g. save to localStorage
    const existingRatings = JSON.parse(localStorage.getItem('recipeRatings') || '{}');
    localStorage.setItem('recipeRatings', JSON.stringify({
      ...existingRatings,
      [props.recipe.id]: tempRating
    }));

    console.log(`Saved ${tempRating / 20} stars for recipe: ${props.recipe.name}`);
  };

  // Load saved rating on first render (optional)
  

  // Styles
  const modalOverlayStyle = {
    position: "fixed",
    top: 0,
    left: 0,
    width: "100vw",
    height: "100vh",
    backgroundColor: "rgba(0, 0, 0, 0.6)",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    zIndex: 1000,
  };

  const modalContentStyle = {
    backgroundColor: "#fff",
    padding: "20px",
    borderRadius: "8px",
    width: "90%",
    maxWidth: "500px",
    maxHeight: "80vh",
    overflowY: "auto",
    position: "relative",
  };

  const closeButtonStyle = {
    position: "absolute",
    top: "10px",
    right: "10px",
    background: "red",
    color: "white",
    border: "none",
    borderRadius: "4px",
    padding: "5px 10px",
    cursor: "pointer",
  };

  return (
    <div style={modalOverlayStyle}>
      <div style={modalContentStyle}>
        <button style={closeButtonStyle} onClick={props?.onClose}>X</button>

        <h2>{props.recipe.name}</h2>

        {/* Rating Logic */}
        <div style={{ marginTop: '10px' }}>
          <button
            onClick={handleRateClick}
            style={{
              padding: '8px 16px',
              backgroundColor: '#ed2929ff',
              color: '#fff',
              border: 'none',
              borderRadius: '4px',
              cursor: 'pointer',
            }}
          >
            Rate Recipe
          </button>
        </div>

        {/* Show rating stars */}
        {showRating && (
          <div style={{ marginTop: '10px', textAlign: 'center' }}>
            <Rating
              onClick={handleRating}
              size={30}
              initialValue={tempRating}
              allowHover
              allowFraction={false}
              transition
              fillColor="#FFD700"
              emptyColor="#fff"
              SVGstyle={{ stroke: 'black', strokeWidth: 2,margin: 2 }}
            />

            <button
              onClick={handleSave}
              style={{
                marginTop: '10px',
                padding: '8px 16px',
                backgroundColor: '#28a745',
                color: '#fff',
                border: 'none',
                borderRadius: '4px',
                cursor: 'pointer',
              }}
              disabled={tempRating === 0}
            >
              Save
            </button>
          </div>
        )}

        {/* Show saved rating if available */}
        {/* {finalRating > 0 && !showRating && (
          <p style={{ textAlign: 'center', marginTop: '10px' }}>
            You rated this recipe: <strong>{finalRating / 20} / 5</strong>
          </p>
        )} */}

        {/* Recipe info */}
        <p><strong>Cuisine:</strong> {props.recipe.cuisine}</p>
        <p><strong>Difficulty Level:</strong> {props.recipe.difficulty}</p>
        <p><strong>Time:</strong> {props.recipe.time} hour</p>
        <p><strong>Instructions: </strong>{props.recipe.instructions}</p>

        <p><strong>Ingredients:</strong></p>
        <ul>
          {props?.recipe.ingredients.map((ingredient, index) => (
            <li key={index}>{ingredient}</li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default RecipeDetailsModal;

