import { Navigate, Route, BrowserRouter as Router, Routes } from 'react-router-dom';
import './App.css';
import Header from './Components/Header';
import { AuthProvider } from './utils/Auth';
import HomePage from './Pages/HomePage';
import LoginPage from './Pages/LoginPage';
import SignupPage from './Pages/SignupPage';
import RecipeForm from './Components/RecipeForm';

function App() {
  return (
    <Router>
      <AuthProvider>
        <Header />
        <main style={{ minHeight: "80vh", padding: "20px" }}>
        <Routes>
          {/* Default route */}
          <Route path="/" element={<Navigate to="/" />} />

          {/* Login & Signup only if NOT logged in */}
          {!user && <Route path="/login" element={<LoginPage />} />}
          {!user && <Route path="/signup" element={<SignupPage />} />}

          {/* Redirect if already logged in */}
          {user && <Route path="/login" element={<Navigate to="/" />} />}
          {user && <Route path="/signup" element={<Navigate to="/" />} />}

          {/* Home (public or protected, depending on your logic) */}
          <Route
            path="/"
            element={
              <Navigate to="/"/>
            }
          />

          {/* Protected Add Recipe */}
         <Route
            path="/addRecipe"
            element={
              <Navigate to="/addRecipe"/>
            }
          />
        </Routes>
      </main>
        {/* <Routes>
          <Route path="/" element={<HomePage />} />
          <Route path="/login" element={<LoginPage />} />
          <Route path="/signup" element={<SignupPage />} />
          <Route path="/addRecipe" element={<RecipeForm/>} />
        </Routes> */}
      </AuthProvider>
    </Router>
  );
}

export default App;

