import React, { useState } from 'react';
import RecipeDetailsModal from './RecipeDetailsModal';

const RecipeCard = ({ recipe }) => {
  const [hours, minutes] = recipe.time.split(":");
  const formattedTime = `${parseInt(hours, 10)}:${minutes.padStart(2, "0")}`;

  return (
    <div
      onClick={props?.onClick}
      style={{
        border: '1px solid #ddd',
        padding: '16px',
        borderRadius: '8px',
        backgroundColor: '#fff',
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'space-between',
        cursor: 'pointer',
        transition: 'box-shadow 0.3s',
      }}
    >
      <div>
        <h3>{recipe.name}</h3>
        <p><b>Cuisine:</b> {recipe.cuisine}</p>
        <p><b>Cooking Time:</b> {formattedTime}</p> {/* Show the formatted time */}
      </div>

      {/* Show buttons if the user owns the recipe */}
      
        <div
          onClick={(e) => e.stopPropagation()}
          style={{
            marginTop: '10px',
            display: 'flex',
            justifyContent: 'space-between'
          }}
        >
          <button onClick={props?.onEdit} style={{ backgroundColor: '#f0ad4e' }}>Edit</button>
          <button onClick={props?.onDelete} style={{ backgroundColor: '#d9534f' }}>Delete</button>
        </div>
      
    </div>
  );
};

export default RecipeCard;
