import React, { useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import { AuthContext } from '../utils/Auth';

const RecipeForm = ({ recipe = {} }) => {
  const navigate = useNavigate();
  const { user } = useContext(AuthContext);

  // Default values for the form (directly from props or empty if new)
  const name = recipe.name || "";
  const cuisine = recipe.cuisine || "";
  const instructions = recipe.instructions || "";
  const time = recipe.time || "0:00";
  const difficulty = recipe.difficulty || "Easy";
  const ingredients = recipe.ingredients || [""];

  const handleSubmit = (e) => {
    e.preventDefault();

    // Parsing time to hours and minutes
    const [hours, minutes] = time.split(":").map((num) => parseInt(num, 10));
    const paddedMinutes = minutes.toString().padStart(2, '0');
    const timeString = `${hours}:${paddedMinutes}`;

    const newRecipe = {
      id: recipe.id || Date.now(), // Ensure an ID exists (for new recipes)
      name,
      cuisine,
      instructions,
      time: timeString,
      difficulty,
      ingredients: ingredients.filter((ing) => ing.trim() !== ""),
      userEmail: user.email
    };

    onAddOrEdit(newRecipe); // Add or Edit the recipe
    navigate('/'); // Navigate to the homepage after submission
  };

  const onAddOrEdit = (newRecipe) => {
    const existingRecipes = JSON.parse(localStorage.getItem('recipes')) || [];
    if (recipe.id) {
      // Editing an existing recipe
      const updatedRecipes = existingRecipes.map(r =>
        r.id === recipe.id ? newRecipe : r
      );
      localStorage.setItem('recipes', JSON.stringify(updatedRecipes));
    } else {
      // Adding a new recipe
      localStorage.setItem('recipes', JSON.stringify([...existingRecipes, newRecipe]));
    }
  };

  const handleIngredientChange = (index, value) => {
    ingredients[index] = value;
    // Update ingredients directly
  };

  const addIngredient = () => {
    ingredients.push('');
  };

  const removeIngredient = (index) => {
    ingredients.splice(index, 1);
  };

  return (
    <form onSubmit={handleSubmit} className="container" style={{ maxWidth: '600px' }}>
      <h2>{recipe.id ? 'Edit Recipe' : 'Add Recipe'}</h2>

      <label>Recipe Name:</label>
      <input 
        defaultValue={name} 
        required 
      />

      <label>Cuisine Type:</label>
      <select 
        defaultValue={cuisine} 
        required
      >
        <option value="">Select cuisine</option>
        <option>Italian</option>
        <option>Mexican</option>
        <option>Asian</option>
      </select>

      <label>Instructions:</label>
      <textarea 
        defaultValue={instructions} 
        rows={4} 
        required
      />

      <label>Cooking Time:</label>
      <div style={{ display: 'flex', gap: '10px', alignItems: 'center' }}>
        <div>
          <input 
            type="number" 
            min="0" 
            defaultValue={parseInt(time.split(":")[0], 10)} 
            required 
            placeholder="Hours"
          />
        </div>

        <div>
          <input 
            type="number" 
            min="0" 
            max="59" 
            defaultValue={parseInt(time.split(":")[1], 10)} 
            required 
            placeholder="Minutes"
          />
        </div>
      </div>

      <div style={{ display: 'flex', alignItems: 'center', gap: '1rem', marginBottom: '10px' }}>
        <label style={{ whiteSpace: 'nowrap' }}>Difficulty Level:</label>

        <div style={{ display: 'flex', gap: '1rem' }}>
          <label>
            <input type="radio" name="difficulty" value="Easy" checked={difficulty === "Easy"} />
            Easy
          </label>

          <label>
            <input type="radio" name="difficulty" value="Medium" checked={difficulty === "Medium"} />
            Medium
          </label>

          <label>
            <input type="radio" name="difficulty" value="Hard" checked={difficulty === "Hard"} />
            Hard
          </label>
        </div>
      </div>

      <label>Ingredients:</label>
      {ingredients.map((ingredient, index) => (
        <div key={index} style={{ display: 'flex', alignItems: 'center', gap: '0.5rem', marginBottom: '5px' }}>
          <input
            type="text"
            defaultValue={ingredient}
            onChange={(e) => handleIngredientChange(index, e.target.value)}
            placeholder={`Ingredient ${index + 1}`}
            style={{ flex: 1 }}
            required
          />
          {ingredients.length > 1 && (
            <button
              type="button"
              onClick={() => removeIngredient(index)}
              style={{
                background: 'red',
                color: 'white',
                border: 'none',
                borderRadius: '50%',
                padding: '8px',
                width: '30px',
                height: '30px',
                cursor: 'pointer',
              }}
              aria-label={`Delete ingredient ${index + 1}`}
            >
              X
            </button>
          )}
        </div>
      ))}

      <div style={{ marginBottom: '15px', display: 'flex', gap: '10px' }}>
        <button type="button" onClick={addIngredient}>Add Ingredient</button>
      </div>

      <button type="submit" style={{ marginBottom: '20px' }}>
        {recipe.id ? 'Update Recipe' : 'Add Recipe'}
      </button>
    </form>
  );
};

export default RecipeForm;
