import React, { useState, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import { AuthContext } from '../utils/Auth';

const RecipeForm = ({ recipe = {} }) => {
  const navigate = useNavigate();
  const { user } = useContext(AuthContext);

  // State hooks for form inputs (now using useState to manage them)
  const [name, setName] = useState(recipe?.name || "");
  const [cuisine, setCuisine] = useState(recipe?.cuisine || "");
  const [instructions, setInstructions] = useState(recipe?.instructions || "");
  
  // Ensure that hours and minutes are initialized with valid values
  const [hours, setHours] = useState(recipe?.time ? Math.floor(parseInt(recipe.time.split(":")[0])) : 0);
  const [minutes, setMinutes] = useState(recipe?.time ? parseInt(recipe.time.split(":")[1]) : 0);
  
  const [difficulty, setDifficulty] = useState(recipe?.difficulty || "Easy");
  const [ingredients, setIngredients] = useState(recipe?.ingredients || [""]);

  const handleSubmit = (e) => {
    e.preventDefault();
    const paddedMinutes = minutes.toString().padStart(2, '0');
    const timeString = `${hours}:${paddedMinutes}`;

    const newRecipe = {
      id: recipe?.id || Date.now(),  // Ensure an ID exists (for new recipes)
      name,
      cuisine,
      instructions,
      time: timeString,
      difficulty,
      ingredients: ingredients.filter((ing) => ing.trim() !== ""),
      userEmail: user.email
    };

    onAddOrEdit(newRecipe); // Add or Edit the recipe
    navigate('/'); // Navigate to the homepage after submission
  };

  const onAddOrEdit = (newRecipe) => {
    const existingRecipes = JSON.parse(localStorage.getItem('recipes')) || [];
    if (recipe.id) {
      // Editing an existing recipe
      const updatedRecipes = existingRecipes.map(r =>
        r.id === recipe.id ? newRecipe : r
      );
      localStorage.setItem('recipes', JSON.stringify(updatedRecipes));
    } else {
      // Adding a new recipe
      localStorage.setItem('recipes', JSON.stringify([...existingRecipes, newRecipe]));
    }
  };

  const handleIngredientChange = (index, value) => {
    const updatedIngredients = [...ingredients];
    updatedIngredients[index] = value;
    setIngredients(updatedIngredients);
  };

  const addIngredient = () => {
    setIngredients([...ingredients, '']);
  };

  const removeIngredient = (index) => {
    const updatedIngredients = ingredients.filter((_, i) => i !== index);
    setIngredients(updatedIngredients);
  };

  return (
    <form onSubmit={handleSubmit} className="container" style={{ maxWidth: '600px' }}>
      <h2>{recipe.id ? 'Edit Recipe' : 'Add Recipe'}</h2>

      <label>Recipe Name:</label>
      <input 
        type="text"
        value={name}
        onChange={(e) => setName(e.target.value)} 
        required 
      />

      <label>Cuisine Type:</label>
      <select 
        value={cuisine} 
        onChange={(e) => setCuisine(e.target.value)} 
        required
      >
        <option value="">Select cuisine</option>
        <option>Italian</option>
        <option>Mexican</option>
        <option>Asian</option>
      </select>

      <label>Instructions:</label>
      <textarea 
        value={instructions}
        onChange={(e) => setInstructions(e.target.value)} 
        rows={4} 
        required
      />

      <label>Cooking Time:</label>
      <div style={{ display: 'flex', gap: '10px', alignItems: 'center' }}>
        <div>
          <input 
            type="number" 
            min="0" 
            value={hours} 
            onChange={(e) => setHours(Number(e.target.value))} 
            required 
            placeholder="Hours"
          />
        </div>

        <div>
          <input 
            type="number" 
            min="0" 
            max="59" 
            value={minutes} 
            onChange={(e) => setMinutes(Math.min(Number(e.target.value), 59))} 
            required 
            placeholder="Minutes"
          />
        </div>
      </div>

      <div style={{ display: 'flex', alignItems: 'center', gap: '1rem', marginBottom: '10px' }}>
        <label style={{ whiteSpace: 'nowrap' }}>Difficulty Level:</label>

        <div style={{ display: 'flex', gap: '1rem' }}>
          <label>
            <input type="radio" name="difficulty" value="Easy" checked={difficulty === "Easy"} onChange={() => setDifficulty("Easy")} />
            Easy
          </label>

          <label>
            <input type="radio" name="difficulty" value="Medium" checked={difficulty === "Medium"} onChange={() => setDifficulty("Medium")} />
            Medium
          </label>

          <label>
            <input type="radio" name="difficulty" value="Hard" checked={difficulty === "Hard"} onChange={() => setDifficulty("Hard")} />
            Hard
          </label>
        </div>
      </div>

      <label>Ingredients:</label>
      {ingredients.map((ingredient, index) => (
        <div key={index} style={{ display: 'flex', alignItems: 'center', gap: '0.5rem', marginBottom: '5px' }}>
          <input
            type="text"
            value={ingredient}
            onChange={(e) => handleIngredientChange(index, e.target.value)}
            placeholder={`Ingredient ${index + 1}`}
            style={{ flex: 1 }}
            required
          />
          {ingredients.length > 1 && (
            <button
              type="button"
              onClick={() => removeIngredient(index)}
              style={{
                background: 'red',
                color: 'white',
                border: 'none',
                borderRadius: '50%',
                padding: '8px',
                width: '30px',
                height: '30px',
                cursor: 'pointer',
              }}
              aria-label={`Delete ingredient ${index + 1}`}
            >
              X
            </button>
          )}
        </div>
      ))}

      <div style={{ marginBottom: '15px', display: 'flex', gap: '10px' }}>
        <button type="button" onClick={addIngredient}>Add Ingredient</button>
      </div>

      <button type="submit" style={{ marginBottom: '20px' }}>
        {recipe.id ? 'Update Recipe' : 'Add Recipe'}
      </button>
    </form>
  );
};

export default RecipeForm;
