import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const RecipeForm = (props) => {
  const initialData = props?.initialData || {}; // Add or edit recipe

  // Use destructuring to split the time into hours and minutes if initialData.time exists
  const [name, setName] = useState(initialData?.name || "");
  const [cuisine, setCuisine] = useState(initialData?.cuisine || "");
  const [instructions, setInstructions] = useState(initialData?.instructions || "");
  const [hours, setHours] = useState(initialData?.time ? Math.floor(initialData.time / 60) : 0);
  const [minutes, setMinutes] = useState(initialData?.time ? initialData.time % 60 : 0);
  const [difficulty, setDifficulty] = useState(initialData?.difficulty || "Easy");
  const [ingredients, setIngredients] = useState(initialData?.ingredients || [""]);
  const [isEditable, setIsEditable] = useState(true);
  const navigate = useNavigate();

  const handleIngredientChange = (index, value) => {
    const updatedIngredients = [...ingredients];
    updatedIngredients[index] = value;
    setIngredients(updatedIngredients);
  };

  const addIngredient = () => {
    setIngredients([...ingredients, '']);
  };

  const removeIngredient = (index) => {
    const updatedIngredients = ingredients.filter((_, i) => i !== index);
    setIngredients(updatedIngredients);
  };

  const onAdd = (newRecipe) => {
    const existingRecipes = JSON.parse(localStorage.getItem("recipes")) || [];
    
    if (props?.initialData) {
      // Edit mode: update existing recipe
      const updatedRecipes = existingRecipes.map((recipe) =>
        recipe.name === props.initialData.name ? newRecipe : recipe
      );
      localStorage.setItem("recipes", JSON.stringify(updatedRecipes));
    } else {
      // Add mode: append new recipe
      localStorage.setItem("recipes", JSON.stringify([...existingRecipes, newRecipe]));
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const paddedMinutes = minutes.toString().padStart(2, '0');
    const timeString = `${hours}:${paddedMinutes}`;
    const newRecipe = {
      name,
      cuisine,
      instructions,
      time: timeString,
      difficulty,
      ingredients: ingredients.filter((ing) => ing.trim() !== "")
    };

    onAdd(newRecipe);
    setName('');
    setCuisine('');
    setInstructions('');
    setHours(0);
    setMinutes(0);
    setDifficulty('Easy');
    setIngredients(['']);
    setIsEditable(true);
    navigate('/');
  };

  return (
    <form onSubmit={handleSubmit} className="container" style={{ maxWidth: '600px' }}>
      <h2>{props?.initialData ? 'Edit Recipe' : 'Add New Recipe'}</h2>

      <label>Recipe Name:</label>
      <input value={name} onChange={(e) => setName(e.target.value)} required />

      <label>Cuisine Type:</label>
      <select value={cuisine} onChange={(e) => setCuisine(e.target.value)} required>
        <option value="">Select cuisine</option>
        <option>Italian</option>
        <option>Mexican</option>
        <option>Asian</option>
      </select>

      <label>Instructions:</label>
      <textarea
        value={instructions}
        onChange={(e) => setInstructions(e.target.value)}
        rows={4}
        required
      />

      <label>Cooking Time:</label>
      <div style={{ display: 'flex', gap: '10px', alignItems: 'center' }}>
        <div>
          <input
            type="number"
            min="0"
            value={hours}
            onChange={(e) => setHours(Number(e.target.value))}
            required
            placeholder="Hours"
          />
        </div>

        <div>
          <input
            type="number"
            min="0"
            max="59"
            value={minutes}
            onChange={(e) => {
              let val = Number(e.target.value);
              if (val > 59) val = 59;
              setMinutes(val);
            }}
            required
            placeholder="Minutes"
          />
        </div>
      </div>

      <div style={{ display: 'flex', alignItems: 'center', gap: '1rem', marginBottom: '10px' }}>
        <label style={{ whiteSpace: 'nowrap' }}>Difficulty Level:</label>

        <div style={{ display: 'flex', gap: '1rem' }}>
          <label style={{ display: 'flex', alignItems: 'center', gap: '0.3rem' }}>
            <input type="radio" name="difficulty" value="Easy" checked={difficulty === "Easy"} onChange={() => setDifficulty("Easy")} />
            Easy
          </label>

          <label style={{ display: 'flex', alignItems: 'center', gap: '0.3rem' }}>
            <input type="radio" name="difficulty" value="Medium" checked={difficulty === "Medium"} onChange={() => setDifficulty("Medium")} />
            Medium
          </label>

          <label style={{ display: 'flex', alignItems: 'center', gap: '0.3rem' }}>
            <input type="radio" name="difficulty" value="Hard" checked={difficulty === "Hard"} onChange={() => setDifficulty("Hard")} />
            Hard
          </label>
        </div>
      </div>

      <label>Ingredients:</label>
      {ingredients.map((ingredient, index) => (
        <div key={index} style={{ display: 'flex', alignItems: 'center', gap: '0.5rem', marginBottom: '5px' }}>
          <input
            type="text"
            value={ingredient}
            onChange={(e) => handleIngredientChange(index, e.target.value)}
            placeholder={`Ingredient ${index + 1}`}
            style={{ flex: 1 }}
            readOnly={!isEditable}
            required={isEditable}
          />
          {ingredients.length > 1 && (
            <button
              type="button"
              onClick={() => removeIngredient(index)}
              style={{
                background: 'red',
                color: 'white',
                border: 'none',
                borderRadius: '50%',
                width: '30px',
                height: '30px',
                cursor: 'pointer',
                alignItems: 'center',
                justifyContent: 'center'
              }}
              aria-label={`Delete ingredient ${index + 1}`}
            >
              X
            </button>
          )}
        </div>
      ))}

      <div style={{ marginBottom: '15px', display: 'flex', gap: '10px' }}>
        <button type="button" onClick={addIngredient}>
          Add Ingredient
        </button>
      </div>

      <button type="submit" style={{ marginBottom: '20px' }}>
        {props?.initialData ? 'Update Recipe' : 'Add Recipe'}
      </button>
    </form>
  );
};

export default RecipeForm;
