import React, { useEffect, useState, useContext } from 'react';
import { Rating } from 'react-simple-star-rating';
import { AuthContext } from '../utils/Auth';

const RecipeDetailsModal = (props) => {
  const { user } = useContext(AuthContext);

  const [showRating, setShowRating] = useState(false);
  const [tempRating, setTempRating] = useState(0);//choose user rating
  const [originalRating, setOriginalRating] = useState(0);//starting ma user rating
  const [saveMessage, setSaveMessage] = useState('');
  const [loading, setLoading] = useState(false);
  const [ratingLoaded, setRatingLoaded] = useState(false);

  useEffect(() => {//state reset
  if (!props?.recipe || !user?.email) {
    // Modal closed or no user: reset states
    setTempRating(0);
    setOriginalRating(0);
    setShowRating(false);
    setRatingLoaded(false);
    setSaveMessage('');
    return;
  }

  // Modal opened - fetch user rating fresh
  const fetchUserRating = async () => {
    try {
      const res = await fetch(`http://localhost:5000/api/recipes/${props.recipe._id}`);
      const data = await res.json();
      console.log('Fetched recipe ratings:', data.recipe.rating);
      const userRatingObj = data.recipe.rating?.find(r => r.userEmail === user.email);
      const value = userRatingObj ? userRatingObj.rating : 0;

      setTempRating(value);
      setOriginalRating(value);
      setSaveMessage('');
    } catch (err) {
      console.error('Error fetching user rating:', err);
      setTempRating(0);
      setOriginalRating(0);
    } finally {
      setRatingLoaded(true);
      setShowRating(false);
    }
  };

  fetchUserRating();
}, [props?.recipe, user?.email]);

  const handleRatingChange = (rate) => {
    setTempRating(rate);
  };

  const handleSave = async () => {
    setLoading(true);
    try {
      const response = await fetch(`http://localhost:5000/api/recipes/${props.recipe._id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          userEmail: user?.email,
          rating: tempRating,
        }),
      });

      if (!response.ok) throw new Error('Failed to save rating');

      setOriginalRating(tempRating);
      setShowRating(false);
      setSaveMessage('Rating Saved!');
      if (props?.onRatingUpdate) props.onRatingUpdate();
    } catch (error) {
      console.error('Error saving rating:', error);
      setSaveMessage('Failed to save rating.');
    } finally {
      setLoading(false);
    }
  };


  const rateButtonLabel = ratingLoaded
    ? originalRating > 0
      ? 'Edit Recipe Rating'
      : 'Add Recipe Rating'
    : 'Loading...';


  // useEffect(() => {
  //   if (ratingLoaded) {
  //     console.log("=== Rating Debug Info ===");
  //     console.log("originalRating:", originalRating);
  //     console.log("tempRating:", tempRating);
  //     console.log("rateButtonLabel:", rateButtonLabel);
  //     console.log("Condition:", originalRating > 0 ? "Editing existing rating" : "Adding new rating");
  //     console.log("=========================");
  //   }
  // }, [ratingLoaded, originalRating, tempRating]);

  if (!props?.recipe) return null;

  return (
    <div style={{
      position: "fixed",
      top: 0,
      left: 0,
      width: "100vw",
      height: "100vh",
      backgroundColor: "rgba(0,0,0,0.6)",
      display: "flex",
      justifyContent: "center",
      alignItems: "center",
      zIndex: 1000,
    }}>
      <div style={{
        backgroundColor: "#fff",
        padding: "20px",
        borderRadius: "8px",
        width: "90%",
        maxWidth: "500px",
        maxHeight: "80vh",
        overflowY: "auto",
        position: "relative",
      }}>
        <button style={{
          position: "absolute",
          top: "10px",
          right: "10px",
          background: "red",
          color: "white",
          border: "none",
          borderRadius: "4px",
          padding: "5px 10px",
          cursor: "pointer",
        }} onClick={props?.onClose}>X</button>

        <h2>{props?.recipe.name}</h2>

        {ratingLoaded && (
          <button
            onClick={() => setShowRating(true)}
            style={{
              marginTop: '10px',
              padding: '8px 16px',
              backgroundColor: '#ed2929ff',
              color: '#fff',
              border: 'none',
              borderRadius: '4px',
              cursor: 'pointer',
            }}>
            {rateButtonLabel}
          </button>
        )}

        {showRating ? (
          <div style={{ marginTop: '10px' }}>
            <Rating
              onClick={handleRatingChange}
              size={30}
              initialValue={tempRating}
              allowHover
              allowFraction={false}
              transition
              fillColor="#FFD700"
              emptyColor="#fff"
              SVGstyle={{ stroke: 'black', strokeWidth: 2, margin: 2 }}
            />
            <button
              onClick={handleSave}
              style={{
                marginTop: '10px',
                padding: '8px 16px',
                backgroundColor: '#28a745',
                color: '#fff',
                border: 'none',
                borderRadius: '4px',
                cursor: 'pointer',
              }}>
              {loading ? 'Saving...' : 'Save Rating'}
            </button>
          </div>
        ) : (
          ratingLoaded && tempRating > 0 && (
            <div style={{ marginTop: '10px' }}>
              <p><strong>Your Rating:</strong></p>
              <Rating
                readonly
                size={25}
                initialValue={tempRating}
                fillColor="#FFD700"
                emptyColor="#fff"
                SVGstyle={{ stroke: 'black', strokeWidth: 2, margin: 2 }}
              />
            </div>
          )
        )}

        <p><strong>Cuisine:</strong> {props?.recipe.cuisine}</p>
        <p><strong>Difficulty Level:</strong> {props?.recipe.difficulty}</p>
        <p><strong>Time:</strong> {props?.recipe.time} hour</p>
        <p><strong>Instructions:</strong> {props?.recipe.instructions}</p>

        <p><strong>Ingredients:</strong></p>
        <ul>
          {props?.recipe.ingredients.map((ingredient, index) => (
            <li key={index}>{ingredient}</li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default RecipeDetailsModal;
