import React, { useEffect, useState } from 'react';
import { Rating } from 'react-simple-star-rating';

const RecipeDetailsModal = (props) => {
  const [showRating,setShowRating] = useState(false);
  const [tempRating, setTempRating] = useState(0);
  const [finalRating, setFinalRating] = useState(0);

  useEffect(() => {
    const initialRating = props?.recipe?.rating || 0;
    setFinalRating(props?.recipe?.rating || 0);
    setTempRating(props?.recipe?.rating || 0);
    setShowRating(false);
  }, [props?.recipe]);

  if (!props?.recipe) return null;

  const rateButtonLabel = finalRating > 0 ? "Edit recipe Rate" : "Add recipe Rate";

  const modalOverlayStyle = {
    position: "fixed",
    top: 0,
    left: 0,
    width: "100vw",
    height: "100vh",
    backgroundColor: "rgba(0, 0, 0, 0.6)",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    zIndex: 1000,
  };

  const modalContentStyle = {
    backgroundColor: "#fff",
    padding: "20px",
    borderRadius: "8px",
    width: "90%",
    maxWidth: "500px",
    maxHeight: "80vh",
    overflowY: "auto",
    position: "relative",
  };

  const closeButtonStyle = {
    position: "absolute",
    top: "10px",
    right: "10px",
    background: "red",
    color: "white",
    border: "none",
    borderRadius: "4px",
    padding: "5px 10px",
    cursor: "pointer",
  };

  // const handleClose = () => {
  //   setIsOpen(false);
  // };

  const handleRateButtonClick = () => {
      setShowRating(true);
  }

  const handleRatingChange = (rate) => {
    setTempRating(rate);
  };

  const handleSave = () => {
    const recipes = JSON.parse(localStorage.getItem('recipes')) || [];
    const updatedRecipes = recipes.map((r) =>
      r.id === props?.recipe.id ? { ...r, rating: tempRating } : r
    );
    localStorage.setItem('recipes', JSON.stringify(updatedRecipes));
    setFinalRating(tempRating);
    setShowRating(false);
    if (props?.onRatingUpdate) props?.onRatingUpdate();
  };

  return (
    <div style={modalOverlayStyle}>
      <div style={modalContentStyle}>
        <button style={closeButtonStyle} onClick={props?.onClose}>X</button>
        <h2>{props.recipe.name}</h2>
        <div style={{ marginTop: '10px' }}>
          <button
            onClick={handleRateButtonClick}
              style={{padding: '8px 16px',backgroundColor: '#ed2929ff',color: '#fff',border: 'none',borderRadius: '4px',
              cursor: 'pointer',}}>
            {rateButtonLabel}
          </button>
        </div>
        {/* Show rating stars */}
        {showRating && (
          <div style={{ marginTop: '10px'}}>
            <Rating
              onClick={handleRatingChange}
              size={30}
              initialValue={tempRating}
              allowHover
              allowFraction={false}
              transition
              fillColor="#FFD700"
              emptyColor="#fff"
              SVGstyle={{ stroke: 'black', strokeWidth: 2,margin: 2 }}
            />

            <button
              onClick={handleSave}
              style={{
                marginTop: '10px',
                padding: '8px 16px',
                backgroundColor: '#28a745',
                color: '#fff',
                border: 'none',
                borderRadius: '4px',
                cursor: 'pointer',
              }}
              disabled={tempRating === 0}
            >
              Save
            </button>
          </div>
        )}

        {/* {finalRating > 0 && (
          <div style={{ marginTop: '10px' }}>
            <p><strong>Your Rating:</strong></p>
            <Rating
              readonly
              size={25}
              initialValue={finalRating}
              fillColor="#FFD700"
              emptyColor="#fff"
              SVGstyle={{ stroke: 'black', strokeWidth: 2, margin: 2 }}
            />
          </div>
        )} */}
        
        <p><strong>Cuisine:</strong> {props.recipe.cuisine}</p>
        <p><strong>Difficulty Level:</strong> {props.recipe.difficulty}</p>
        <p><strong>Time:</strong> {props.recipe.time} hour</p>
        <p><strong>Instructions: </strong>{props.recipe.instructions}</p>
        {/* Ingredients can be displayed here if needed */}
        <p>Ingredients:</p>
        <ul>
          {props?.recipe.ingredients.map((ingredient, index) => (
            <li key={index}>{ingredient}</li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default RecipeDetailsModal;
