import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import RecipeCard from '../Components/RecipeCard';
import { AuthContext } from '../utils/Auth';
import RecipeDetailsModal from '../Components/RecipeDetailsModal';

const HomePage = () => {
  const navigate = useNavigate();
  const { user } = React.useContext(AuthContext);
  const [recipes, setRecipes] = useState([]);
  const [filteredRecipes, setFilteredRecipes] = useState([]);
  const [showOnlyMyRecipe, setShowOnlyMyRecipe] = useState(false);
  const [selectedRecipe, setSelectedRecipe] = useState(null);
  
  // For filtering
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedCuisine, setSelectedCuisine] = useState('');
  const [selectedDifficulty, setSelectedDifficulty] = useState('');
  const [sortBy, setSortBy] = useState(''); // 'time' or 'name'

  useEffect(() => {
    // Load recipes from localStorage or API
    const savedRecipes = JSON.parse(localStorage.getItem('recipes')) || [];
    setRecipes(savedRecipes);
    setFilteredRecipes(savedRecipes);
  }, []);

  useEffect(() => {
    let filtered = recipes;

    // Filter by My Recipes toggle
    if (showOnlyMyRecipe && user) {
      filtered = filtered.filter((recipe) => recipe.userEmail === user.email);
    }

    // Search by name
    if (searchQuery) {
      filtered = filtered.filter((recipe) =>
        recipe.name.toLowerCase().includes(searchQuery.toLowerCase())
      );
    }

    // Filter by cuisine
    if (selectedCuisine) {
      filtered = filtered.filter((recipe) => recipe.cuisine === selectedCuisine);
    }

    // Filter by difficulty
    if (selectedDifficulty) {
      filtered = filtered.filter((recipe) => recipe.difficulty === selectedDifficulty);
    }

    // Sort by cooking time or name
    if (sortBy === 'time') {
      filtered = filtered.sort((a, b) => {
        const timeA = a.time.split(':');
        const timeB = b.time.split(':');
        const totalMinutesA = parseInt(timeA[0]) * 60 + parseInt(timeA[1]);
        const totalMinutesB = parseInt(timeB[0]) * 60 + parseInt(timeB[1]);
        return totalMinutesA - totalMinutesB;
      });
    } else if (sortBy === 'name') {
      filtered = filtered.sort((a, b) => a.name.localeCompare(b.name));
    }

    setFilteredRecipes(filtered);
  }, [searchQuery, showOnlyMyRecipe, selectedCuisine, selectedDifficulty, sortBy, recipes, user]);

  const handleDelete = (recipeId) => {
    // Filter out the deleted recipe by ID
    const updatedRecipes = recipes.filter((recipe) => recipe.id !== recipeId);

    // Update state with the new list
    setRecipes(updatedRecipes);
    setFilteredRecipes(updatedRecipes);

    // Update localStorage with the new list
    localStorage.setItem('recipes', JSON.stringify(updatedRecipes));
  };

  return (
    <div>
      {/* User logged in, show the buttons for adding and filtering recipes */}
      {user && (
        <div style={{ display: 'flex', gap: '10px', marginBottom: '10px' }}>
          <button onClick={() => setShowOnlyMyRecipe((prev) => !prev)}>
            {showOnlyMyRecipe ? 'Show All Recipes' : 'Show My Recipes'}
          </button>
          <button
            onClick={() => navigate('/addRecipe')} // Navigate to the form to add a new recipe
          >
            Add Recipe
          </button>
        </div>
      )}

      {/* Filters */}
      <div style={{ marginBottom: '20px', display: 'flex', gap: '15px' }}>
        <input
          type="text"
          placeholder="Search by recipe name"
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          style={{ padding: '8px', width: '200px' }}
        />

        <select
          value={selectedCuisine}
          onChange={(e) => setSelectedCuisine(e.target.value)}
          style={{ padding: '8px' }}
        >
          <option value="">Filter by Cuisine</option>
          <option value="Italian">Italian</option>
          <option value="Mexican">Mexican</option>
          <option value="Asian">Asian</option>
        </select>

        <select
          value={selectedDifficulty}
          onChange={(e) => setSelectedDifficulty(e.target.value)}
          style={{ padding: '8px' }}
        >
          <option value="">Filter by Difficulty</option>
          <option value="Easy">Easy</option>
          <option value="Medium">Medium</option>
          <option value="Hard">Hard</option>
        </select>

        <select
          value={sortBy}
          onChange={(e) => setSortBy(e.target.value)}
          style={{ padding: '8px' }}
        >
          <option value="">Sort By</option>
          <option value="name">Name</option>
          <option value="time">Cooking Time</option>
        </select>
      </div>

      {/* No recipes found message */}
      {filteredRecipes.length === 0 ? (
        <p>No recipes found. Try adjusting your filters or add one!</p>
      ) : (
        <div style={gridStyle}>
          {/* Render recipe cards */}
          {filteredRecipes.map((r) => (
            <RecipeCard
              key={r.id}
              recipe={r}
              onDelete={handleDelete} // Pass handleDelete to RecipeCard
            />
          ))}
        </div>
      )}

      {/* Show Recipe Details Modal */}
      {selectedRecipe && (
        <RecipeDetailsModal
          recipe={selectedRecipe}
          onClose={() => setSelectedRecipe(null)} // Close the modal
        />
      )}
    </div>
  );
};

const gridStyle = {
  display: 'grid',
  gridTemplateColumns: 'repeat(auto-fill, minmax(250px, 1fr))',
  gap: '20px',
};

export default HomePage;
