// RecipeDetailsModal.js
import React, { useState, useEffect } from 'react';
import { Rating } from 'react-simple-star-rating';

const RecipeDetailsModal = ({ recipe, onClose, setAllRecipes }) => {
  const [showRating, setShowRating] = useState(false);
  const [tempRating, setTempRating] = useState(0);
  const [finalRating, setFinalRating] = useState(recipe?.rating || 0);

  useEffect(() => {
    setFinalRating(recipe?.rating || 0);
  }, [recipe]);

  if (!recipe) return null;

  const handleRateClick = () => {
    setShowRating(true);
  };

  const handleRatingChange = (rate) => {
    setTempRating(rate);
  };

  const handleSave = () => {
    const recipes = JSON.parse(localStorage.getItem("recipes")) || [];

    const updatedRecipes = recipes.map((r) =>
      r.id === recipe.id ? { ...r, rating: tempRating } : r
    );

    localStorage.setItem("recipes", JSON.stringify(updatedRecipes));
    setAllRecipes(updatedRecipes); // Update parent state

    setFinalRating(tempRating);
    setShowRating(false);
  };

  return (
    <div style={{
      position: "fixed", top: 0, left: 0, width: "100vw", height: "100vh",
      backgroundColor: "rgba(0, 0, 0, 0.6)", display: "flex",
      justifyContent: "center", alignItems: "center", zIndex: 1000
    }}>
      <div style={{
        backgroundColor: "#fff", padding: "20px", borderRadius: "8px",
        width: "90%", maxWidth: "500px", maxHeight: "80vh", overflowY: "auto",
        position: "relative"
      }}>
        <button
          style={{
            position: "absolute", top: "10px", right: "10px",
            background: "red", color: "white", border: "none",
            borderRadius: "4px", padding: "5px 10px", cursor: "pointer"
          }}
          onClick={onClose}
        >
          X
        </button>
        <h2>{recipe.name}</h2>
        <button
          onClick={handleRateClick}
          style={{
            padding: '8px 16px', backgroundColor: '#ed2929ff',
            color: '#fff', border: 'none', borderRadius: '4px',
            cursor: 'pointer', marginTop: '10px'
          }}
        >
          Rate Recipe
        </button>

        {showRating && (
          <div style={{ marginTop: '10px' }}>
            <Rating
              onClick={handleRatingChange}
              size={30}
              initialValue={tempRating}
              allowHover
              allowFraction={false}
              fillColor="#FFD700"
              emptyColor="#fff"
              SVGstyle={{ stroke: 'black', strokeWidth: 2, margin: 2 }}
            />
            <button
              onClick={handleSave}
              style={{
                marginTop: '10px',
                padding: '8px 16px',
                backgroundColor: '#28a745',
                color: '#fff',
                border: 'none',
                borderRadius: '4px',
                cursor: 'pointer',
              }}
              disabled={tempRating === 0}
            >
              Save
            </button>
          </div>
        )}

        {finalRating > 0 && (
          <div style={{ marginTop: '10px' }}>
            <p><strong>Your Rating:</strong></p>
            <Rating
              readonly
              size={25}
              initialValue={finalRating}
              fillColor="#FFD700"
              emptyColor="#fff"
              SVGstyle={{ stroke: 'black', strokeWidth: 2, margin: 2 }}
            />
          </div>
        )}

        <p><strong>Cuisine:</strong> {recipe.cuisine}</p>
        <p><strong>Difficulty:</strong> {recipe.difficulty}</p>
        <p><strong>Time:</strong> {recipe.time} hour</p>
        <p><strong>Instructions:</strong> {recipe.instructions}</p>
        <p><strong>Ingredients:</strong></p>
        <ul>
          {recipe.ingredients.map((ingredient, i) => (
            <li key={i}>{ingredient}</li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default RecipeDetailsModal;
